DataTable dt = dataset.Tables["A"];\nDataTable filteredTable = new DataTable();\n\n// Add the desired columns to the new DataTable\nfilteredTable.Columns.Add("a3", typeof(dt.Columns["a3"].DataType));\nfilteredTable.Columns.Add("a4", typeof(dt.Columns["a4"].DataType));\n\n// Copy the data from the original DataTable to the new DataTable\nforeach (DataRow row in dt.Rows)\n{\n    DataRow newRow = filteredTable.NewRow();\n    newRow["a3"] = row["a3"];\n    newRow["a4"] = row["a4"];\n    filteredTable.Rows.Add(newRow);\n}\n\n// Bind the new DataTable to the DataGrid\nmyDataGrid.DataSource = filteredTable;\nmyDataGrid.DataBind();