using System;\nusing System.Net;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Load the certificate\n        X509Certificate2 certificate = new X509Certificate2("path_to_certificate.cer");\n\n        // Add the certificate to the trusted root store\n        using (X509Store store = new X509Store(StoreName.Root, StoreLocation.CurrentUser))\n        {\n            store.Open(OpenFlags.ReadWrite);\n            store.Add(certificate);\n            store.Close();\n        }\n\n        // Call the web service\n        CallWebService();\n    }\n\n    static void CallWebService()\n    {\n        // Assuming you have a service reference named MyWebServiceReference\n        MyWebServiceReference.MyWebServiceClient client = new MyWebServiceReference.MyWebServiceClient();\n        client.ClientCredentials.ServiceCertificate.Authentication.CertificateValidationMode = System.ServiceModel.Security.X509CertificateValidationMode.None;\n\n        try\n        {\n            var result = client.MyWebMethod();\n            Console.WriteLine(result);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}