[TestClass]\n     public class MathTests\n     {\n         [TestMethod]\n         public void Add_TwoNumbers_ReturnsSum()\n         {\n             // Arrange\n             var math = new Math();\n             int a = 5;\n             int b = 10;\n\n             // Act\n             int result = math.Add(a, b);\n\n             // Assert\n             Assert.AreEqual(15, result);\n         }\n     }