using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Xml.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string xmlData = @"<Entries>\n                             <MainEntry>\n                               <NM>attack</NM>\n                               <NM>cardiac</NM>\n                               <IE>413.9</IE>\n                             </MainEntry>\n                             <MainEntry>\n                               <IE>557.1</IE>\n                             </MainEntry>\n                             <MainEntry>\n                               <IE>443.9</IE>\n                               <SubEntry>\n                                 <IE>440.20</IE>\n                                 <NM>Arteriosclerosis,extremities</NM>\n                               </SubEntry>\n                             </MainEntry>\n                           </Entries>";\n\n        XElement entriesXml = XElement.Parse(xmlData);\n        var entries = from entry in entriesXml.Elements("MainEntry")\n                      select new Entry\n                      {\n                          NM = entry.Elements("NM").Select(x => x.Value).ToList(),\n                          IE = (string)entry.Element("IE"),\n                          SubEntries = (from subEntry in entry.Elements("SubEntry")\n                                        select new SubEntry\n                                        {\n                                            NM = subEntry.Elements("NM").Select(x => x.Value).ToList(),\n                                            IE = (string)subEntry.Element("IE")\n                                        }).ToList()\n                      };\n\n        foreach (var entry in entries)\n        {\n            Console.WriteLine($"Main Entry IE: {entry.IE}");\n            if (entry.NM != null)\n            {\n                foreach (var nm in entry.NM)\n                {\n                    Console.WriteLine($"NM: {nm}");\n                }\n            }\n            if (entry.SubEntries != null)\n            {\n                foreach (var subEntry in entry.SubEntries)\n                {\n                    Console.WriteLine($"  Sub Entry IE: {subEntry.IE}");\n                    foreach (var subNm in subEntry.NM)\n                    {\n                        Console.WriteLine($"  NM: {subNm}");\n                    }\n                }\n            }\n        }\n    }\n}