public class Program\n{\n    static void Main(string[] args)\n    {\n        // Register the custom validation rules\n        var validationRegistry = new CustomValidationRegistry();\n        validationRegistry.Register();\n\n        // Now you can use the validators\n        var instance = new YourSubsonicClass();\n        var validatorRunner = new ValidatorRunner(ValidatorRegistry.GetInstance());\n\n        if (validatorRunner.IsValid(instance))\n        {\n            // instance is valid\n        }\n        else\n        {\n            // instance is not valid\n            var errors = validatorRunner.GetErrorSummary(instance);\n            foreach (var error in errors)\n            {\n                Console.WriteLine(error);\n            }\n        }\n    }\n}