using System;\nusing System.Collections.Generic;\nusing System.IO;\n\nstatic List<List<string>> ReadFromFile(string filename)\n{\n    StreamReader SR = File.OpenText(filename);\n    string S;\n    string[] S_split;\n\n    List<List<string>> matrix = new List<List<string>>();\n\n    while ((S = SR.ReadLine()) != null)\n    {\n        List<string> row = new List<string>(); // Create a new row for each line\n        S_split = S.Split(' ');\n        for (int i = 0; i < S_split.Length; i++)\n        {\n            row.Add(S_split[i]);\n        }\n        matrix.Add(row);\n    }\n\n    SR.Close();\n    return matrix;\n}\n\n// Example usage\npublic static void Main(string[] args)\n{\n    string filename = "path_to_your_file.txt";\n    List<List<string>> matrix = ReadFromFile(filename);\n\n    // Print the matrix to check the result\n    for (int i = 0; i < matrix.Count; i++)\n    {\n        for (int j = 0; j < matrix[i].Count; j++)\n        {\n            Console.Write(matrix[i][j] + "|");\n        }\n        Console.WriteLine();\n    }\n}