public void CleanupStaleSessions()\n   {\n       using (SqlConnection conn = new SqlConnection(connectionString))\n       {\n           conn.Open();\n           SqlCommand cleanupCmd = new SqlCommand("DELETE FROM UserSessions WHERE LastActivityDate < @ThresholdDate", conn);\n           cleanupCmd.Parameters.AddWithValue("@ThresholdDate", DateTime.Now.AddMinutes(-Session.Timeout));\n           cleanupCmd.ExecuteNonQuery();\n       }\n   }