using System;\nusing System.IO;\nusing System.Net;\n\nclass FtpClient\n{\n    private string ftpServer;\n    private string userName;\n    private string password;\n\n    public FtpClient(string ftpServer, string userName, string password)\n    {\n        this.ftpServer = ftpServer;\n        this.userName = userName;\n        this.password = password;\n    }\n\n    public bool DirectoryExists(string directoryPath)\n    {\n        try\n        {\n            FtpWebRequest request = (FtpWebRequest)WebRequest.Create(directoryPath);\n            request.Method = WebRequestMethods.Ftp.ListDirectory;\n            request.Credentials = new NetworkCredential(userName, password);\n            \n            using (FtpWebResponse response = (FtpWebResponse)request.GetResponse())\n            {\n                return true;\n            }\n        }\n        catch (WebException ex)\n        {\n            FtpWebResponse response = (FtpWebResponse)ex.Response;\n            if (response != null && response.StatusCode == FtpStatusCode.ActionNotTakenFileUnavailable)\n            {\n                return false;\n            }\n            throw;\n        }\n    }\n\n    public void CreateDirectory(string directoryPath)\n    {\n        if (!DirectoryExists(directoryPath))\n        {\n            FtpWebRequest request = (FtpWebRequest)WebRequest.Create(directoryPath);\n            request.Method = WebRequestMethods.Ftp.MakeDirectory;\n            request.Credentials = new NetworkCredential(userName, password);\n\n            using (FtpWebResponse response = (FtpWebResponse)request.GetResponse())\n            {\n                Console.WriteLine($"Create Directory status: {response.StatusDescription}");\n            }\n        }\n        else\n        {\n            Console.WriteLine("Directory already exists.");\n        }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string ftpServer = "ftp://example.com";\n        string userName = "yourUsername";\n        string password = "yourPassword";\n        string directoryPath = "ftp://example.com/newFolder";\n\n        FtpClient ftpClient = new FtpClient(ftpServer, userName, password);\n\n        if (!ftpClient.DirectoryExists(directoryPath))\n        {\n            ftpClient.CreateDirectory(directoryPath);\n            Console.WriteLine("Directory created successfully.");\n        }\n        else\n        {\n            Console.WriteLine("Directory already exists.");\n        }\n    }\n}