public class MyClass\n{\n    public event EventHandler MyEvent;\n\n    public void AttachHandler(EventHandler handler)\n    {\n        MyEvent += handler;\n    }\n\n    public void DetachHandler(EventHandler handler)\n    {\n        MyEvent -= handler;\n    }\n}\n\npublic class Program\n{\n    static void Main()\n    {\n        MyClass myClass = new MyClass();\n        EventHandler handler = (sender, e) => Console.WriteLine("Event triggered");\n\n        myClass.AttachHandler(handler);\n        // Event handler is now attached and will respond to MyEvent\n\n        myClass.DetachHandler(handler);\n        // Event handler is now detached and will no longer respond to MyEvent\n    }\n}