using System.Data.SqlServerCe;\n\nstring connectionString = "Data Source='myDatabase.sdf';";\n\nusing (SqlCeConnection connection = new SqlCeConnection(connectionString))\n{\n    connection.Open();\n\n    string selectQuery = "SELECT * FROM Orders WHERE CustomerID = @CustomerID AND OrderID = @OrderID";\n    using (SqlCeCommand selectCommand = new SqlCeCommand(selectQuery, connection))\n    {\n        selectCommand.Parameters.AddWithValue("@CustomerID", 10);\n        selectCommand.Parameters.AddWithValue("@OrderID", 1234);\n\n        using (SqlCeDataReader reader = selectCommand.ExecuteReader())\n        {\n            if (reader.Read())\n            {\n                // Record found, now update it\n                string updateQuery = "UPDATE Orders SET SomeField = @SomeField WHERE CustomerID = @CustomerID AND OrderID = @OrderID";\n                using (SqlCeCommand updateCommand = new SqlCeCommand(updateQuery, connection))\n                {\n                    updateCommand.Parameters.AddWithValue("@SomeField", "NewValue");\n                    updateCommand.Parameters.AddWithValue("@CustomerID", 10);\n                    updateCommand.Parameters.AddWithValue("@OrderID", 1234);\n\n                    updateCommand.ExecuteNonQuery();\n                }\n            }\n        }\n    }\n}