using System;\nusing System.Collections.Generic;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing System.Web.UI;\n\npublic partial class ConcatPages : Page\n{\n    protected async void Page_Load(object sender, EventArgs e)\n    {\n        List<string> pageUrls = GetPageUrlsFromDatabase();\n\n        foreach (string url in pageUrls)\n        {\n            string content = await FetchPageContentAsync(url);\n            content = ProcessContent(content); // Optionally process the content\n            contentDiv.InnerHtml += content;\n        }\n    }\n\n    private List<string> GetPageUrlsFromDatabase()\n    {\n        // Fetch the URLs from your database\n        // Example:\n        return new List<string>\n        {\n            "http://example.com/page1.aspx",\n            "http://example.com/page2.aspx",\n            "http://example.com/page3.aspx"\n        };\n    }\n\n    private async Task<string> FetchPageContentAsync(string url)\n    {\n        using (HttpClient client = new HttpClient())\n        {\n            HttpResponseMessage response = await client.GetAsync(url);\n            return await response.Content.ReadAsStringAsync();\n        }\n    }\n\n    private string ProcessContent(string content)\n    {\n        // You can perform any processing on the content if needed\n        return content;\n    }\n}