using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        // Load the XML document\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load("path_to_your_xml_file.xml");\n\n        // Define the XPath expression to select the specific node\n        string xpath = "/root/element_to_remove";\n\n        // Select the node using the XPath expression\n        XmlNode nodeToRemove = xmlDoc.SelectSingleNode(xpath);\n\n        // Check if the node exists\n        if (nodeToRemove != null && nodeToRemove.ParentNode != null)\n        {\n            // Remove the node from its parent\n            nodeToRemove.ParentNode.RemoveChild(nodeToRemove);\n            Console.WriteLine("Node removed successfully.");\n        }\n        else\n        {\n            Console.WriteLine("Node not found.");\n        }\n\n        // Optionally, save the modified document\n        xmlDoc.Save("path_to_your_modified_xml_file.xml");\n    }\n}