using System;\nusing System.IO;\nusing System.Web;\n\npublic partial class ImageLoader : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string imageName = "exampleImageName"; // Replace with your image name\n        string imageDirectory = Server.MapPath("~/images"); // Adjust the directory path as needed\n\n        string imagePath = FindImagePath(imageDirectory, imageName);\n\n        if (!string.IsNullOrEmpty(imagePath))\n        {\n            // Display the image\n            ImageControl.ImageUrl = ResolveUrl(imagePath);\n        }\n        else\n        {\n            // Handle the image not found case\n            Response.Write("Image not found");\n        }\n    }\n\n    private string FindImagePath(string directory, string imageName)\n    {\n        var files = Directory.GetFiles(directory, imageName + ".*");\n        if (files.Length > 0)\n        {\n            return files[0];\n        }\n        return null;\n    }\n}