[System.Web.Services.WebMethod]\npublic static string GetTelephone(string personId)\n{\n    // Retrieve the telephone number based on personId from your data source.\n    string telephone = GetTelephoneFromDatabase(personId);\n    return telephone;\n}\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Initial setup if needed.\n    }\n    else\n    {\n        // Retrieve the telephone number from ViewState and set it back to the label.\n        if (ViewState["Telephone"] != null)\n        {\n            lblTelephone.Text = ViewState["Telephone"].ToString();\n        }\n    }\n}\n\nprotected void SomePostbackControl_Click(object sender, EventArgs e)\n{\n    // Save the current label text to ViewState before handling any postback event.\n    ViewState["Telephone"] = hiddenField.Value;\n}