using System;\nusing System.Diagnostics;\nusing System.Web.UI;\n\npublic partial class UserCount : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Counter for current connections to the web service\n        PerformanceCounter connectionCounter = new PerformanceCounter();\n        connectionCounter.CategoryName = "Web Service";\n        connectionCounter.CounterName = "Current Connections";\n        connectionCounter.InstanceName = "_Total";\n\n        // Get the current number of connections\n        float currentConnections = connectionCounter.NextValue();\n\n        // Display the number of current connections on the web page\n        Response.Write("Current number of users connected: " + currentConnections);\n    }\n}