using System;\nusing System.Runtime.InteropServices;\nusing System.Threading;\n\nclass Program\n{\n    [DllImport("kernel32.dll")]\n    private static extern bool QueryPerformanceCounter(out long lpPerformanceCount);\n\n    [DllImport("kernel32.dll")]\n    private static extern bool QueryPerformanceFrequency(out long lpFrequency);\n\n    static void Main(string[] args)\n    {\n        long frequency;\n        QueryPerformanceFrequency(out frequency);\n        \n        Timer timer = new Timer(TimerTick, frequency, 0, 10); // 10 ms interval\n\n        Console.WriteLine("Press Enter to exit...");\n        Console.ReadLine();\n    }\n\n    private static void TimerTick(object state)\n    {\n        long frequency = (long)state;\n        long counter;\n        QueryPerformanceCounter(out counter);\n\n        double milliseconds = (double)counter / frequency * 1000;\n        Console.WriteLine($"Tick: {DateTime.Now:HH:mm:ss.fff} - High Precision: {milliseconds:F3} ms");\n    }\n}