using System;\nusing System.Data.SqlClient;\nusing System.Threading.Tasks;\nusing System.Windows.Forms;\n\npublic class ProgressBarForm : Form\n{\n    private ProgressBar progressBar;\n    private Label statusLabel;\n    private Button startButton;\n\n    public ProgressBarForm()\n    {\n        progressBar = new ProgressBar() { Location = new System.Drawing.Point(20, 20), Width = 200 };\n        statusLabel = new Label() { Location = new System.Drawing.Point(20, 50), Width = 200, Text = "Ready" };\n        startButton = new Button() { Location = new System.Drawing.Point(20, 80), Text = "Start Query" };\n        startButton.Click += async (sender, e) => await RunQueryAsync();\n\n        Controls.Add(progressBar);\n        Controls.Add(statusLabel);\n        Controls.Add(startButton);\n    }\n\n    private async Task RunQueryAsync()\n    {\n        try\n        {\n            progressBar.Style = ProgressBarStyle.Marquee;\n            statusLabel.Text = "Loading...";\n            string connectionString = "your_connection_string_here";\n            using (SqlConnection connection = new SqlConnection(connectionString))\n            {\n                await connection.OpenAsync();\n                string query = "your_sql_query_here";\n                using (SqlCommand command = new SqlCommand(query, connection))\n                {\n                    await command.ExecuteNonQueryAsync();\n                }\n            }\n            statusLabel.Text = "Query Completed";\n        }\n        catch (Exception ex)\n        {\n            statusLabel.Text = $"Error: {ex.Message}";\n        }\n        finally\n        {\n            progressBar.Style = ProgressBarStyle.Blocks;\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new ProgressBarForm());\n    }\n}