using System;\nusing System.IO;\n\npublic class IISResetFileWatcher\n{\n    public static void Main()\n    {\n        string pathToWatch = @"C:\Path\To\Watch";\n        string fileName = "reset_indicator.txt";\n\n        FileSystemWatcher watcher = new FileSystemWatcher();\n        watcher.Path = pathToWatch;\n        watcher.Filter = fileName;\n        watcher.Changed += OnChanged;\n        watcher.EnableRaisingEvents = true;\n        \n        Console.WriteLine("Monitoring file changes for IIS Reset. Press Enter to exit...");\n        Console.ReadLine();\n    }\n\n    private static void OnChanged(object source, FileSystemEventArgs e)\n    {\n        Console.WriteLine("Detected IIS Reset via file change at: " + DateTime.Now);\n        // Add logic to restart Windows Services here\n        RestartWindowsServices();\n    }\n\n    private static void RestartWindowsServices()\n    {\n        // Logic to restart your Windows Services\n        Console.WriteLine("Restarting Windows Services...");\n    }\n}