using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class AesEncryption\n{\n    private static readonly string encryptionKey = "your-encryption-key-32-chars"; // Must be 32 chars for AES-256\n\n    public static string Encrypt(string plainText)\n    {\n        byte[] key = Encoding.UTF8.GetBytes(encryptionKey);\n        using (Aes aesAlg = Aes.Create())\n        {\n            aesAlg.Key = key;\n            aesAlg.GenerateIV();\n            ICryptoTransform encryptor = aesAlg.CreateEncryptor(aesAlg.Key, aesAlg.IV);\n            using (MemoryStream msEncrypt = new MemoryStream())\n            {\n                msEncrypt.Write(aesAlg.IV, 0, aesAlg.IV.Length); // Write IV to the beginning of the stream\n                using (CryptoStream csEncrypt = new CryptoStream(msEncrypt, encryptor, CryptoStreamMode.Write))\n                using (StreamWriter swEncrypt = new StreamWriter(csEncrypt))\n                {\n                    swEncrypt.Write(plainText);\n                }\n                return Convert.ToBase64String(msEncrypt.ToArray());\n            }\n        }\n    }\n\n    public static string Decrypt(string cipherText)\n    {\n        byte[] fullCipher = Convert.FromBase64String(cipherText);\n        byte[] key = Encoding.UTF8.GetBytes(encryptionKey);\n        using (Aes aesAlg = Aes.Create())\n        {\n            aesAlg.Key = key;\n            byte[] iv = new byte[aesAlg.BlockSize / 8];\n            byte[] cipher = new byte[fullCipher.Length - iv.Length];\n            Array.Copy(fullCipher, iv, iv.Length);\n            Array.Copy(fullCipher, iv.Length, cipher, 0, cipher.Length);\n            aesAlg.IV = iv;\n            ICryptoTransform decryptor = aesAlg.CreateDecryptor(aesAlg.Key, aesAlg.IV);\n            using (MemoryStream msDecrypt = new MemoryStream(cipher))\n            using (CryptoStream csDecrypt = new CryptoStream(msDecrypt, decryptor, CryptoStreamMode.Read))\n            using (StreamReader srDecrypt = new StreamReader(csDecrypt))\n            {\n                return srDecrypt.ReadToEnd();\n            }\n        }\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static void Main()\n    {\n        string original = "Sensitive data to encrypt";\n        string encrypted = AesEncryption.Encrypt(original);\n        string decrypted = AesEncryption.Decrypt(encrypted);\n\n        Console.WriteLine($"Original: {original}");\n        Console.WriteLine($"Encrypted: {encrypted}");\n        Console.WriteLine($"Decrypted: {decrypted}");\n    }\n}