using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string xml = "<VAR NAME=\"sf_name\" ID=\"1001210\">\n<VAR NAME=\"sf_name\" ID=\"1001211\">\n</VAR>";\n        \n        // Define the regex pattern to match the ID attribute\n        string pattern = @"(ID\s*=\s*"")(\d+)(\s*"")";\n        \n        // Use Regex.Replace with a callback function to modify the IDs\n        string updatedXml = Regex.Replace(xml, pattern, match =>\n        {\n            int id = int.Parse(match.Groups[2].Value);\n            // Modify the ID as needed (example: increment by 1)\n            id += 1;\n            return $"{match.Groups[1].Value}{id}{match.Groups[3].Value}";\n        });\n\n        Console.WriteLine(updatedXml);\n    }\n}