using System;\nusing System.Drawing;\nusing System.Drawing.Imaging;\nusing HtmlRenderer.PdfSharp;\nusing PdfSharp.Drawing;\nusing PdfSharp.Pdf;\n\npublic class HtmlToTiffConverter\n{\n    public static void ConvertHtmlToTiff(string htmlString, string outputPath)\n    {\n        // Create a PDF document\n        PdfDocument pdf = new PdfDocument();\n        PdfPage pdfPage = pdf.AddPage();\n        XGraphics gfx = XGraphics.FromPdfPage(pdfPage);\n        XSize size = PageSizeConverter.ToSize(PdfSharp.PageSize.A4);\n\n        // Adjust the size of the page to the rendered HTML content\n        gfx.Dispose();\n        pdfPage.Size = PdfSharp.PageSize.A4;\n        pdfPage.Orientation = PdfSharp.PageOrientation.Portrait;\n\n        // Render HTML to PDF\n        HtmlRenderer.PdfSharp.HtmlContainer container = new HtmlRenderer.PdfSharp.HtmlContainer();\n        container.SetHtml(htmlString);\n        container.PerformLayout(gfx);\n\n        // Create a bitmap to render the HTML\n        Bitmap bitmap = new Bitmap((int)size.Width, (int)size.Height);\n        using (Graphics graphics = Graphics.FromImage(bitmap))\n        {\n            container.PerformPaint(graphics);\n        }\n\n        // Save the bitmap as TIFF\n        bitmap.Save(outputPath, ImageFormat.Tiff);\n    }\n\n    public static void Main(string[] args)\n    {\n        string htmlString = "<html><body><h1>Confirmation</h1><p>This is a confirmation message.</p></body></html>";\n        string outputPath = "output.tiff";\n        ConvertHtmlToTiff(htmlString, outputPath);\n        Console.WriteLine("HTML converted to TIFF successfully!");\n    }\n}