using System;\nusing NATUPNPLib;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            UPnPNAT upnpnat = new UPnPNAT();\n            IStaticPortMappingCollection mappings = upnpnat.StaticPortMappingCollection;\n\n            if (mappings != null)\n            {\n                // Assuming there is at least one port mapping\n                foreach (IStaticPortMapping mapping in mappings)\n                {\n                    Console.WriteLine("ExternalIPAddress: " + mapping.ExternalIPAddress);\n                    break;\n                }\n            }\n            else\n            {\n                Console.WriteLine("No Port Mappings found");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}