using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic class StoredProcedureParameters\n{\n    public static void GetStoredProcedureParameters(string connectionString, string storedProcedureName)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            using (SqlCommand command = new SqlCommand(storedProcedureName, connection))\n            {\n                command.CommandType = CommandType.StoredProcedure;\n                \n                // Open the connection\n                connection.Open();\n                \n                // Derive the parameters\n                SqlCommandBuilder.DeriveParameters(command);\n                \n                // Iterate through the parameters and display their details\n                foreach (SqlParameter parameter in command.Parameters)\n                {\n                    Console.WriteLine("Parameter Name: {0}", parameter.ParameterName);\n                    Console.WriteLine("Parameter Type: {0}", parameter.SqlDbType);\n                    Console.WriteLine("Parameter Size: {0}", parameter.Size);\n                    Console.WriteLine("Is Output Parameter: {0}", parameter.Direction == ParameterDirection.Output);\n                    Console.WriteLine();\n                }\n            }\n        }\n    }\n}\n\n// Usage example:\nstring connectionString = "YourConnectionStringHere";\nstring storedProcedureName = "YourStoredProcedureNameHere";\nStoredProcedureParameters.GetStoredProcedureParameters(connectionString, storedProcedureName);