public class ProductController : Controller\n   {\n       private readonly ICrudService<Product> crudService;\n\n       public ProductController(ICrudService<Product> crudService)\n       {\n           this.crudService = crudService;\n       }\n\n       public ActionResult Index()\n       {\n           return View(crudService.GetAll());\n       }\n\n       public ActionResult Details(int id)\n       {\n           var product = crudService.GetById(id);\n           if (product == null)\n           {\n               return HttpNotFound();\n           }\n           return View(product);\n       }\n\n       // Other actions...\n   }