// Example of configuring DataReader Source in SSIS Data Flow Task\nstring connectionString = "your_connection_string";\nusing (var connection = new OracleConnection(connectionString))\n{\n    connection.Open();\n    using (var command = new OracleCommand("SELECT * FROM your_oracle_table", connection))\n    using (var reader = command.ExecuteReader())\n    {\n        while (reader.Read())\n        {\n            // Process each row\n        }\n    }\n}