private void dataGridView1_KeyDown(object sender, KeyEventArgs e)\n{\n    if (e.KeyCode == Keys.Enter)\n    {\n        e.Handled = true; // Prevent the default behavior\n\n        int currentColumn = dataGridView1.CurrentCell.ColumnIndex;\n        int currentRow = dataGridView1.CurrentCell.RowIndex;\n\n        // Move to the next column\n        int nextColumn = currentColumn + 1;\n\n        // Check if we need to wrap to the next row\n        if (nextColumn >= dataGridView1.ColumnCount)\n        {\n            nextColumn = 0;\n            currentRow++;\n        }\n\n        // Check if we are still within the row bounds\n        if (currentRow < dataGridView1.RowCount)\n        {\n            dataGridView1.CurrentCell = dataGridView1.Rows[currentRow].Cells[nextColumn];\n        }\n    }\n}