using System;\nusing System.IO;\nusing System.IO.IsolatedStorage;\n\npublic class IsolatedStorageExample\n{\n    public void SaveData(string data)\n    {\n        using (IsolatedStorageFile isoStore = IsolatedStorageFile.GetUserStoreForAssembly())\n        {\n            using (IsolatedStorageFileStream isoStream = new IsolatedStorageFileStream("example.txt", FileMode.Create, isoStore))\n            {\n                using (StreamWriter writer = new StreamWriter(isoStream))\n                {\n                    writer.WriteLine(data);\n                }\n            }\n        }\n    }\n\n    public string LoadData()\n    {\n        using (IsolatedStorageFile isoStore = IsolatedStorageFile.GetUserStoreForAssembly())\n        {\n            if (isoStore.FileExists("example.txt"))\n            {\n                using (IsolatedStorageFileStream isoStream = new IsolatedStorageFileStream("example.txt", FileMode.Open, isoStore))\n                {\n                    using (StreamReader reader = new StreamReader(isoStream))\n                    {\n                        return reader.ReadToEnd();\n                    }\n                }\n            }\n            else\n            {\n                return null;\n            }\n        }\n    }\n}