using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string[] tableNames = { "Table1", "Table2", "Table3" }; // Add all your table names here\n\n        DataSet dataSet = new DataSet();\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            foreach (string tableName in tableNames)\n            {\n                SqlDataAdapter adapter = new SqlDataAdapter($"SELECT * FROM {tableName} WHERE 1 = 0", connection); // Query to get schema only\n                adapter.FillSchema(dataSet, SchemaType.Source, tableName);\n            }\n        }\n\n        // At this point, dataSet contains schema (table definitions, constraints, and relationships) of the specified tables\n    }\n}