protected void GridView1_SelectedIndexChanged(object sender, EventArgs e)\n{\n    GridViewRow row = GridView1.SelectedRow;\n    Label1.Text = row.Cells[1].Text;\n\n    using (OleDbConnection myConnection = new OleDbConnection("Provider=Microsoft.Jet.OLEDB.4.0;Data Source=" + dbpath + "/secure_user/data/data.mdb"))\n    {\n        string query = "SELECT tasks.Task, tasks.Priority, tasks.Done, tasks.Hours " +\n                       "FROM tasks " +\n                       "INNER JOIN projects ON tasks.Project = projects.Project " +\n                       "WHERE tasks.Username = ? AND tasks.Project = ?";\n        \n        using (OleDbDataAdapter adapter = new OleDbDataAdapter(query, myConnection))\n        {\n            adapter.SelectCommand.Parameters.AddWithValue("@Username", uname);\n            adapter.SelectCommand.Parameters.AddWithValue("@Project", Label1.Text);\n            \n            DataTable table = new DataTable();\n            adapter.Fill(table);\n            GridView2.DataSource = table;\n            GridView2.DataBind();\n            GridView2.Visible = true;\n        }\n    }\n}