using OfficeOpenXml;\n   using OfficeOpenXml.FormulaParsing;\n\n   public void ReadAndEvaluateExcel(string filePath)\n   {\n       using (var package = new ExcelPackage(new FileInfo(filePath)))\n       {\n           var worksheet = package.Workbook.Worksheets[0];\n           var cell = worksheet.Cells["A1"];\n           var formula = cell.Formula;\n           var value = cell.Value;\n           // Evaluate formula if necessary\n           var parser = new ExcelFormulaParser(package.Workbook);\n           var parsedFormula = parser.Parse(formula);\n           var result = parsedFormula.Evaluate();\n           Console.WriteLine($"Formula: {formula}, Value: {result}");\n       }\n   }