using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        DataTable myTable = new DataTable();\n        myTable.Columns.Add("Id", typeof(int));\n        myTable.Columns.Add("Name", typeof(string));\n\n        // Add rows to the DataTable\n        myTable.Rows.Add(1, "John Doe");\n        myTable.Rows.Add(2, "Jane Smith");\n\n        string connectionString = "your_connection_string";\n\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            conn.Open();\n\n            using (SqlCommand cmd = new SqlCommand("dbo.InsertMyTableType", conn))\n            {\n                cmd.CommandType = CommandType.StoredProcedure;\n\n                SqlParameter param = cmd.Parameters.AddWithValue("@MyTable", myTable);\n                param.SqlDbType = SqlDbType.Structured;\n                param.TypeName = "dbo.MyTableType";\n\n                cmd.ExecuteNonQuery();\n            }\n        }\n    }\n}