public class AdminController : Controller\n   {\n       private readonly ApplicationDbContext _context;\n\n       public AdminController(ApplicationDbContext context)\n       {\n           _context = context;\n       }\n\n       public ActionResult GetHiredEmployees(int semesterId)\n       {\n           var hiredEmployees = _context.HiredEmployees\n               .Where(he => he.SemesterId == semesterId)\n               .Select(he => he.User)\n               .ToList();\n\n           return PartialView("_HiredEmployeesList", hiredEmployees);\n       }\n   }