using System;\nusing System.ComponentModel;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        WebClient client = new WebClient();\n\n        // Subscribe to the UploadProgressChanged event\n        client.UploadProgressChanged += new UploadProgressChangedEventHandler(UploadProgressCallback);\n        // Subscribe to the UploadFileCompleted event\n        client.UploadFileCompleted += new UploadFileCompletedEventHandler(UploadFileCompletedCallback);\n\n        Uri uri = new Uri("https://yourserver.com/upload");\n        string filePath = "path_to_your_file";\n\n        try\n        {\n            // Start the file upload\n            client.UploadFileAsync(uri, filePath);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n\n    private static void UploadProgressCallback(object sender, UploadProgressChangedEventArgs e)\n    {\n        Console.WriteLine("Upload {0}% complete. {1} of {2} bytes transferred.",\n            e.ProgressPercentage,\n            e.BytesSent,\n            e.TotalBytesToSend);\n    }\n\n    private static void UploadFileCompletedCallback(object sender, UploadFileCompletedEventArgs e)\n    {\n        if (e.Cancelled)\n        {\n            Console.WriteLine("Upload canceled.");\n        }\n        else if (e.Error != null)\n        {\n            Console.WriteLine("Error: " + e.Error.Message);\n        }\n        else\n        {\n            Console.WriteLine("Upload completed successfully.");\n        }\n    }\n}