public partial class StudentForm : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            List<Student> students = new List<Student>();\n            Session["Students"] = students;\n            BindData();\n        }\n    }\n\n    private void BindData()\n    {\n        List<Student> students = Session["Students"] as List<Student>;\n        Repeater1.DataSource = students;\n        Repeater1.DataBind();\n    }\n\n    protected void btnAddStudent_Click(object sender, EventArgs e)\n    {\n        List<Student> students = Session["Students"] as List<Student>;\n        int newId = students.Count > 0 ? students.Max(s => s.ID) + 1 : 1;\n        students.Add(new Student { ID = newId });\n        Session["Students"] = students;\n        BindData();\n    }\n\n    protected void btnSave_Click(object sender, EventArgs e)\n    {\n        List<Student> students = new List<Student>();\n        \n        foreach (RepeaterItem item in Repeater1.Items)\n        {\n            int id = Convert.ToInt32((item.FindControl("txtID") as TextBox).Text);\n            string firstName = (item.FindControl("txtFirstName") as TextBox).Text;\n            string lastName = (item.FindControl("txtLastName") as TextBox).Text;\n            string email = (item.FindControl("txtEmail") as TextBox).Text;\n\n            students.Add(new Student\n            {\n                ID = id,\n                FirstName = firstName,\n                LastName = lastName,\n                Email = email\n            });\n        }\n\n        Session["Students"] = students;\n        // Redirect or proceed to the next step\n    }\n}