using System;\n\npublic class Program\n{\n    public static int SumOfDigits(int number)\n    {\n        int sum = 0;\n        while (number != 0)\n        {\n            sum += number % 10;\n            number /= 10;\n        }\n        return sum;\n    }\n\n    public static void Main()\n    {\n        int number = 17463;\n        Console.WriteLine($"Sum of digits in {number} = {SumOfDigits(number)}");\n    }\n}