using System;\nusing System.Data;\n\npublic class XmlToDataSet\n{\n    public static void Main()\n    {\n        string xmlFilePath = "path_to_your_complex_xml.xml";\n        \n        // Create a new DataSet\n        DataSet dataSet = new DataSet();\n\n        // Load the XML file into the DataSet\n        dataSet.ReadXml(xmlFilePath);\n\n        // Optional: Display the DataSet contents for verification\n        foreach (DataTable table in dataSet.Tables)\n        {\n            Console.WriteLine("Table: " + table.TableName);\n            foreach (DataRow row in table.Rows)\n            {\n                foreach (DataColumn column in table.Columns)\n                {\n                    Console.Write(row[column] + "\t");\n                }\n                Console.WriteLine();\n            }\n            Console.WriteLine();\n        }\n\n        // Optional: Display the relationships for verification\n        foreach (DataRelation relation in dataSet.Relations)\n        {\n            Console.WriteLine("Relation: " + relation.RelationName);\n            Console.WriteLine("Parent Table: " + relation.ParentTable.TableName);\n            Console.WriteLine("Child Table: " + relation.ChildTable.TableName);\n            Console.WriteLine();\n        }\n    }\n}