// C# Web API to get resource strings\n[Route("api/Localization/GetResources")]\npublic class LocalizationController : ApiController\n{\n    [HttpGet]\n    public IHttpActionResult GetResources(string culture)\n    {\n        var resourceSet = Resources.ResourceManager.GetResourceSet(new CultureInfo(culture), true, true);\n        var resources = resourceSet.Cast<DictionaryEntry>().ToDictionary(entry => entry.Key.ToString(), entry => entry.Value.ToString());\n        return Ok(resources);\n    }\n}