using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\nnamespace OracleConnectionExample\n{\n    public partial class MainForm : Form\n    {\n        public MainForm()\n        {\n            InitializeComponent();\n        }\n\n        private void CheckDatabase()\n        {\n            try\n            {\n                StringBuilder str = new StringBuilder();\n                string connectionString = Properties.Settings.Default.OracleConnectionString;\n\n                using (OracleConnection con = new OracleConnection(connectionString))\n                {\n                    using (OracleCommand cmd = new OracleCommand("SELECT timestamp FROM jde_out WHERE rownum = 1", con))\n                    {\n                        con.Open();\n                        using (OracleDataReader rdr = cmd.ExecuteReader())\n                        {\n                            if (rdr.Read())\n                            {\n                                str.AppendLine(rdr["timestamp"].ToString());\n                            }\n                        }\n                    }\n                }\n\n                this.lblJDEtime.Text = str.ToString();\n            }\n            catch (OracleException err)\n            {\n                MessageBox.Show("Exception caught:\n\n" + err.ToString());\n            }\n        }\n    }\n}