public static class ControllerExtensions\n{\n    public static ActionResult GetActionResult(this Controller controller, FormResponse formResponse)\n    {\n        if (controller.Request.Form["noscript"] == "true")\n        {\n            // Serialize the FormResponse to the Session\n            controller.Session["FormResponse"] = formResponse;\n            // Redirect to a different action or URL\n            return controller.Redirect("/desired-redirect-url");\n        }\n        else\n        {\n            // Set the Model to the JSONed FormResponse and return a View\n            controller.ViewData.Model = formResponse.ToJSON();\n            return controller.View();\n        }\n    }\n}