using System;\nusing System.Collections.Generic;\n\npublic class WordReader\n{\n    private readonly string _data;\n    private int _position;\n\n    public WordReader(string data)\n    {\n        _data = data;\n        _position = 0;\n    }\n\n    public string ReadWord()\n    {\n        if (_position >= _data.Length)\n            return null;\n\n        // Skip leading whitespace\n        while (_position < _data.Length && char.IsWhiteSpace(_data[_position]))\n            _position++;\n\n        // Return null if we reach the end of the string\n        if (_position >= _data.Length)\n            return null;\n\n        // Find the end of the word\n        int start = _position;\n        while (_position < _data.Length && !char.IsWhiteSpace(_data[_position]))\n            _position++;\n\n        string word = _data.Substring(start, _position - start);\n        return word;\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        string data = "Four score and seven years ago";\n        List<string> words = new List<string>();\n        WordReader reader = new WordReader(data);\n\n        while (true)\n        {\n            string word = reader.ReadWord();\n            if (string.IsNullOrEmpty(word)) break;\n            // Additional parsing logic goes here\n            words.Add(word);\n        }\n\n        // Output the words\n        foreach (string word in words)\n        {\n            Console.WriteLine(word);\n        }\n    }\n}