using System;\nusing System.Collections.Generic;\nusing System.Data.SqlClient;\nusing System.Web.Script.Serialization;\n\npublic class Record\n{\n    public string Id { get; set; }\n    public string Name { get; set; }\n    public string Address { get; set; }\n    public string City { get; set; }\n}\n\npublic class DatabaseService\n{\n    public List<Record> GetRecordsFromDatabase()\n    {\n        List<Record> records = new List<Record>();\n\n        string connectionString = "your_connection_string_here";\n        string query = "SELECT Id, Name, Address, City FROM YourTable";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand command = new SqlCommand(query, connection);\n            connection.Open();\n            using (SqlDataReader reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    Record record = new Record\n                    {\n                        Id = reader["Id"].ToString(),\n                        Name = reader["Name"].ToString(),\n                        Address = reader["Address"].ToString(),\n                        City = reader["City"].ToString()\n                    };\n                    records.Add(record);\n                }\n            }\n        }\n        return records;\n    }\n\n    public string GetRecordsAsJson()\n    {\n        List<Record> records = GetRecordsFromDatabase();\n        JavaScriptSerializer jsSerializer = new JavaScriptSerializer();\n        return jsSerializer.Serialize(records);\n    }\n}