using (SqlConnection conn = new SqlConnection(connectionString))\n{\n    using (SqlCommand cmd = new SqlCommand("GetBusinessMessage", conn))\n    {\n        cmd.CommandType = CommandType.StoredProcedure;\n        \n        cmd.Parameters.AddWithValue("@UserId", userId);\n\n        SqlParameter messageParam = new SqlParameter("@Message", SqlDbType.NVarChar, 255);\n        messageParam.Direction = ParameterDirection.Output;\n        cmd.Parameters.Add(messageParam);\n\n        conn.Open();\n        cmd.ExecuteNonQuery();\n\n        string userMessage = (string)messageParam.Value;\n        Console.WriteLine(userMessage); // Display the message to the user\n    }\n}