using System;\nusing System.IO;\nusing System.Web;\nusing System.Web.Mvc;\n\npublic ActionResult Upload(HttpPostedFileBase file, string modificationDate)\n{\n    if (file != null && !string.IsNullOrEmpty(modificationDate))\n    {\n        string path = Server.MapPath("~/UploadedFiles/" + Path.GetFileName(file.FileName));\n        file.SaveAs(path);\n\n        // Parse the modification date\n        DateTime modDate;\n        if (DateTime.TryParse(modificationDate, out modDate))\n        {\n            File.SetLastWriteTime(path, modDate);\n            File.SetCreationTime(path, modDate);\n        }\n        return Content("File uploaded successfully with correct modification date.");\n    }\n\n    return Content("Failed to upload file.");\n}