<%@ WebHandler Language="C#" Class="UploadHandler" %>\n\nusing System;\nusing System.Web;\n\npublic class UploadHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        context.Response.ContentType = "text/plain";\n\n        if (context.Request.Files.Count > 0)\n        {\n            HttpPostedFile uploadedFile = context.Request.Files[0];\n\n            if (uploadedFile != null && uploadedFile.ContentLength > 0)\n            {\n                string savePath = context.Server.MapPath("~/uploads/") + uploadedFile.FileName;\n\n                // Ensure the uploads directory exists, create if necessary\n                System.IO.Directory.CreateDirectory(context.Server.MapPath("~/uploads/"));\n\n                // Save the uploaded file\n                uploadedFile.SaveAs(savePath);\n\n                context.Response.Write("File uploaded successfully: " + uploadedFile.FileName);\n            }\n            else\n            {\n                context.Response.Write("No file uploaded.");\n            }\n        }\n        else\n        {\n            context.Response.Write("No file uploaded.");\n        }\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}