protected void Application_AuthenticateRequest(Object sender, EventArgs e)\n{\n    #if DEBUG\n        var customIdentity = new CustomIdentity(@"desiredDomain\otherUserName");\n        var customPrincipal = new CustomPrincipal(customIdentity);\n        HttpContext.Current.User = customPrincipal;\n    #else\n        var windowsIdentity = (WindowsIdentity)HttpContext.Current.User.Identity;\n        var customPrincipal = new CustomPrincipal(windowsIdentity);\n        HttpContext.Current.User = customPrincipal;\n    #endif\n}