using System.Security.Cryptography.X509Certificates;\nusing System.Security.Permissions;\n\npublic void ImportCertificate(string pfxFilePath, string password, StoreName storeName, StoreLocation storeLocation)\n{\n    // Load the certificate with the exportable flag\n    X509Certificate2 cert = new X509Certificate2(pfxFilePath, password, X509KeyStorageFlags.Exportable);\n\n    X509Store certStore = new X509Store(storeName, storeLocation);\n    StorePermission permission = new StorePermission(PermissionState.Unrestricted);\n    permission.Flags = StorePermissionFlags.AddToStore;\n    permission.Assert();\n\n    certStore.Open(OpenFlags.ReadWrite);\n    certStore.Add(cert);\n    certStore.Close();\n}