using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client; // Make sure you have the Oracle.ManagedDataAccess library\n\npublic class OracleProcedureCaller\n{\n    public void ExecuteStoredProcedure(string connectionString)\n    {\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            connection.Open();\n            using (OracleCommand command = new OracleCommand("FII_DBO.CLEAR_UNIT_TEST_PRODUCT", connection))\n            {\n                command.CommandType = CommandType.StoredProcedure;\n                try\n                {\n                    command.ExecuteNonQuery();\n                    Console.WriteLine("Procedure executed successfully.");\n                }\n                catch (OracleException ex)\n                {\n                    Console.WriteLine("Oracle error: " + ex.Message);\n                }\n            }\n        }\n    }\n}\n\n// Usage example:\n// string connectionString = "your_connection_string_here";\n// new OracleProcedureCaller().ExecuteStoredProcedure(connectionString);