using System;\nusing System.Net;\nusing System.IO;\n\npublic class KofaxToSharePoint\n{\n    public void UploadDocument(string filePath, string sharePointUrl, string library)\n    {\n        try\n        {\n            WebClient client = new WebClient();\n            client.Credentials = CredentialCache.DefaultCredentials;\n\n            byte[] fileData = File.ReadAllBytes(filePath);\n            client.UploadData($"{sharePointUrl}/_layouts/15/SP.RequestExecutor.ashx?Target={library}", "POST", fileData);\n        }\n        catch (Exception ex)\n        {\n            // Log error\n            Console.WriteLine($"Error uploading document: {ex.Message}");\n        }\n    }\n}