using System;\nusing RestSharp;\n\npublic class Program\n{\n    public static void Main()\n    {\n        var client = new RestClient("http://example.com");\n        var request = new RestRequest("resource", Method.GET);\n\n        // To store and manage cookies\n        client.CookieContainer = new System.Net.CookieContainer();\n\n        // Making the first request\n        IRestResponse response = client.Execute(request);\n        Console.WriteLine(response.Content);\n\n        // Making a subsequent request with the same client, cookies will be sent automatically\n        var anotherRequest = new RestRequest("another-resource", Method.POST);\n        anotherRequest.AddParameter("paramName", "paramValue");\n        IRestResponse anotherResponse = client.Execute(anotherRequest);\n        Console.WriteLine(anotherResponse.Content);\n    }\n}