public ActionResult GetRecord(int id)\n{\n    var record = _repository.GetRecordById(id);\n    if (record == null)\n    {\n        if (Request.IsAjaxRequest())\n        {\n            return Json(new { success = false, message = "Record not found" });\n        }\n        return HttpNotFound();\n    }\n\n    if (Request.IsAjaxRequest())\n    {\n        return Json(new { success = true, data = record });\n    }\n    return View(record);\n}